	public static function doUpdate($values, PropelPDO $con = null)
	{
		if ($con === null) {
			$con = Propel::getConnection(sfSimpleCMSSlotPeer::DATABASE_NAME, Propel::CONNECTION_WRITE);
		}

		$selectCriteria = new Criteria(self::DATABASE_NAME);

		if ($values instanceof Criteria) {
			$criteria = clone $values; // rename for clarity

			$comparison = $criteria->getComparison(sfSimpleCMSSlotPeer::PAGE_ID);
			$value = $criteria->remove(sfSimpleCMSSlotPeer::PAGE_ID);
			if ($value) {
				$selectCriteria->add(sfSimpleCMSSlotPeer::PAGE_ID, $value, $comparison);
			} else {
				$selectCriteria->setPrimaryTableName(sfSimpleCMSSlotPeer::TABLE_NAME);
			}

			$comparison = $criteria->getComparison(sfSimpleCMSSlotPeer::CULTURE);
			$value = $criteria->remove(sfSimpleCMSSlotPeer::CULTURE);
			if ($value) {
				$selectCriteria->add(sfSimpleCMSSlotPeer::CULTURE, $value, $comparison);
			} else {
				$selectCriteria->setPrimaryTableName(sfSimpleCMSSlotPeer::TABLE_NAME);
			}

			$comparison = $criteria->getComparison(sfSimpleCMSSlotPeer::NAME);
			$value = $criteria->remove(sfSimpleCMSSlotPeer::NAME);
			if ($value) {
				$selectCriteria->add(sfSimpleCMSSlotPeer::NAME, $value, $comparison);
			} else {
				$selectCriteria->setPrimaryTableName(sfSimpleCMSSlotPeer::TABLE_NAME);
			}

		} else { // $values is sfSimpleCMSSlot object
			$criteria = $values->buildCriteria(); // gets full criteria
			$selectCriteria = $values->buildPkeyCriteria(); // gets criteria w/ primary key(s)
		}

		// set the correct dbName
		$criteria->setDbName(self::DATABASE_NAME);

		return BasePeer::doUpdate($selectCriteria, $criteria, $con);
