	/**
	 * Retrieve object using using composite pkey values.
	 * @param      int $page_id
	 * @param      string $culture
	 * @param      string $name
	 * @param      PropelPDO $con
	 * @return     sfSimpleCMSSlot
	 */
	public static function retrieveByPK($page_id, $culture, $name, PropelPDO $con = null) {
		$_instancePoolKey = serialize(array((string) $page_id, (string) $culture, (string) $name));
 		if (null !== ($obj = sfSimpleCMSSlotPeer::getInstanceFromPool($_instancePoolKey))) {
 			return $obj;
		}

		if ($con === null) {
			$con = Propel::getConnection(sfSimpleCMSSlotPeer::DATABASE_NAME, Propel::CONNECTION_READ);
		}
		$criteria = new Criteria(sfSimpleCMSSlotPeer::DATABASE_NAME);
		$criteria->add(sfSimpleCMSSlotPeer::PAGE_ID, $page_id);
		$criteria->add(sfSimpleCMSSlotPeer::CULTURE, $culture);
		$criteria->add(sfSimpleCMSSlotPeer::NAME, $name);
		$v = sfSimpleCMSSlotPeer::doSelect($criteria, $con);

		return !empty($v) ? $v[0] : null;
	}
