	/**
	 * Soft delete records, given a sfSimpleCMSSlot or Criteria object OR a primary key value.
	 *
	 * @param			 mixed $values Criteria or sfSimpleCMSSlot object or primary key or array of primary keys
	 *							which is used to create the DELETE statement
	 * @param			 PropelPDO $con the connection to use
	 * @return		 int	The number of affected rows (if supported by underlying database driver).
	 * @throws		 PropelException Any exceptions caught during processing will be
	 *							rethrown wrapped into a PropelException.
	 */
	public static function doSoftDelete($values, PropelPDO $con = null)
	{
		if ($values instanceof Criteria) {
			// rename for clarity
			$criteria = clone $values;
		} elseif ($values instanceof sfSimpleCMSSlot) {
			// create criteria based on pk values
			$criteria = $values->buildPkeyCriteria();
		} else {
			// it must be the primary key
			$criteria = new Criteria(self::DATABASE_NAME);
			$criteria->add(sfSimpleCMSSlotPeer::PAGE_ID, $values[0], Criteria::EQUAL);
			$criteria->add(sfSimpleCMSSlotPeer::CULTURE, $values[1], Criteria::EQUAL);
			$criteria->add(sfSimpleCMSSlotPeer::NAME, $values[2], Criteria::EQUAL);
		}
		$criteria->add(sfSimpleCMSSlotPeer::DELETED_AT, time());
		return sfSimpleCMSSlotPeer::doUpdate($criteria, $con);
	}
	
